src/App.css.content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Inter', sans-serif;
  line-height: 1.6;
  color: #333;
  overflow-x: hidden;
}

*,
*::before,
*::after {
  transition: all 0.3s ease;
}

::selection {
  background: #3498db;
  color: white;
}

::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(45deg, #3498db, #2980b9);
  border-radius: 10px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(45deg, #2980b9, #1abc9c);
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
}

.section-title {
  text-align: center;
  font-size: 2.5rem;
  color: #ffffff;
  margin-bottom: 3rem;
  font-weight: 600;
  position: relative;
  opacity: 0;
  animation: fadeInScale 1s ease-out both;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
}

.section-title::after {
  content: '';
  position: absolute;
  bottom: -10px;
  left: 50%;
  transform: translateX(-50%);
  width: 80px;
  height: 4px;
  background: linear-gradient(45deg, #00ffff, #ff0096);
  border-radius: 2px;
  animation: gradient 3s ease infinite;
  box-shadow: 0 0 10px rgba(0, 255, 255, 0.5);
}

html {
  scroll-behavior: smooth;
}

/* Responsive Typography */
@media (max-width: 768px) {
  .section-title {
    font-size: 2rem;
  }

  .container {
    padding: 0 1rem;
  }
}

@media (max-width: 480px) {
  .section-title {
    font-size: 1.8rem;
  }
}

/* Loading animation for images */
img {
  transition: all 0.3s ease;
}

img:hover {
  transform: scale(1.02);
}

/* Button hover effects */
button {
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

button:hover {
  transform: translateY(-2px);
}

/* Link animations */
a {
  transition: all 0.3s ease;
}

/* Form animations */
input, textarea, select {
  transition: all 0.3s ease;
}

/* Card hover effects */
.card {
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.card:hover {
  transform: translateY(-5px);
}

/* Fade in animation for sections */
section {
  opacity: 0;
  animation: fadeIn 1s ease-out both;
}

section:nth-of-type(1) { animation-delay: 0s; }
section:nth-of-type(2) { animation-delay: 0.2s; }
section:nth-of-type(3) { animation-delay: 0.4s; }
section:nth-of-type(4) { animation-delay: 0.6s; }
section:nth-of-type(5) { animation-delay: 0.8s; }

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeInScale {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes gradient {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

