}

src/components/About.css.content:
.about {
  padding: 80px 0;
  background: linear-gradient(135deg, #764ba2 0%, #667eea 100%);
  overflow: hidden;
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes fadeInScale {
  from {
    opacity: 0;
    transform: scale(0.8);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes gradient {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

.about .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
}

.section-title {
  text-align: center;
  font-size: 2.5rem;
  color: #e0e0e0;
  margin-bottom: 3rem;
  font-weight: 600;
  position: relative;
  animation: fadeInScale 1s ease-out;
}

.section-title::after {
  content: '';
  position: absolute;
  bottom: -10px;
  left: 50%;
  transform: translateX(-50%);
  width: 80px;
  height: 4px;
  background: linear-gradient(45deg, #f093fb, #f5576c, #f093fb);
  background-size: 200% 100%;
  border-radius: 2px;
  animation: gradient 3s ease infinite;
}

.about-content {
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 4rem;
  align-items: center;
}

.about-text {
  animation: slideInLeft 1s ease-out;
}

.about-text h3 {
  font-size: 1.8rem;
  color: #e0e0e0;
  margin-bottom: 1.5rem;
  opacity: 0;
  animation: fadeInScale 1s ease-out 0.3s both;
}

.about-text p {
  color: #bbb;
  line-height: 1.8;
  margin-bottom: 1.5rem;
  font-size: 1.1rem;
  opacity: 0;
  animation: slideInLeft 1s ease-out 0.5s both;
}

.about-text p:nth-child(3) {
  animation-delay: 0.7s;
}

.personal-info {
  background: rgba(17, 153, 142, 0.2);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(17, 153, 142, 0.3);
  padding: 2rem;
  border-radius: 15px;
  box-shadow: 0 5px 25px rgba(0,0,0,0.2);
  margin-top: 2rem;
  opacity: 0;
  animation: fadeInScale 1s ease-out 0.9s both;
  transition: all 0.3s ease;
}

.personal-info:hover {
  transform: translateY(-5px);
  box-shadow: 0 15px 40px rgba(0,0,0,0.25);
}

.info-item {
  display: flex;
  justify-content: space-between;
  padding: 0.8rem 0;
  border-bottom: 1px solid #333;
  transition: all 0.3s ease;
  opacity: 0;
  animation: slideInLeft 0.6s ease-out both;
}

.info-item:nth-child(1) { animation-delay: 1.1s; }
.info-item:nth-child(2) { animation-delay: 1.2s; }
.info-item:nth-child(3) { animation-delay: 1.3s; }
.info-item:nth-child(4) { animation-delay: 1.4s; }

.info-item:hover {
  background: rgba(17, 153, 142, 0.3);
  padding-left: 1rem;
  border-radius: 8px;
}

.info-item:last-child {
  border-bottom: none;
}

.info-item strong {
  color: #e0e0e0;
}

.about-image {
  display: flex;
  justify-content: center;
  animation: slideInRight 1s ease-out 0.6s both;
}

.image-frame {
  width: 300px;
  height: 400px;
  background: linear-gradient(135deg, #11998e 0%, #38ef7d 100%);
  background-size: 200% 200%;
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  box-shadow: 0 15px 50px rgba(17, 153, 142, 0.4);
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  animation: gradient 5s ease infinite;
}

.profile-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 20px;
  transition: all 0.4s ease;
}

.image-overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(135deg, rgba(17, 153, 142, 0.8) 0%, rgba(56, 239, 125, 0.8) 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 4rem;
  color: white;
  opacity: 0;
  transition: all 0.4s ease;
  border-radius: 20px;
}

.image-frame:hover .image-overlay {
  opacity: 1;
}

.image-frame:hover {
  transform: scale(1.05) rotate(2deg);
  box-shadow: 0 25px 70px rgba(17, 153, 142, 0.6);
}

.image-frame:hover .profile-image {
  transform: scale(1.1);
}

@media (max-width: 768px) {
  .about-content {
    grid-template-columns: 1fr;
    gap: 2rem;
    text-align: center;
  }

  .section-title {
    font-size: 2rem;
  }

  .about-text h3 {
    font-size: 1.5rem;
  }

  .image-frame {
    width: 200px;
    height: 200px;
    font-size: 3rem;
  }
}

src/components/Contact.css.content:
.contact {
  padding: 80px 0;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  position: relative;
  overflow: hidden;
}

.contact::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="20" cy="20" r="3" fill="rgba(52,152,219,0.1)"/><circle cx="80" cy="80" r="2" fill="rgba(52,152,219,0.1)"/></svg>');
  animation: float 12s ease-in-out infinite;
}

@keyframes contactSlideIn {
  from {
    opacity: 0;
    transform: translateX(-50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes contactSlideInRight {
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes iconPulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.1); }
}

@keyframes formFocus {
  0% { box-shadow: 0 0 0 0 rgba(52, 152, 219, 0.4); }
  70% { box-shadow: 0 0 0 10px rgba(52, 152, 219, 0); }
  100% { box-shadow: 0 0 0 0 rgba(52, 152, 219, 0); }
}

.contact .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 1;
}

.contact-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  margin-top: 3rem;
}

.contact-info {
  animation: contactSlideIn 1s ease-out;
}

.contact-info h3 {
  font-size: 1.8rem;
  color: #2c3e50;
  margin-bottom: 1rem;
  opacity: 0;
  animation: fadeInScale 1s ease-out 0.3s both;
}

.contact-info p {
  color: #555;
  line-height: 1.6;
  margin-bottom: 2rem;
  opacity: 0;
  animation: contactSlideIn 1s ease-out 0.5s both;
}

.contact-item {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1.5rem;
  opacity: 0;
  animation: contactSlideIn 0.8s ease-out both;
  transition: all 0.3s ease;
}

.contact-item:nth-child(3) { animation-delay: 0.7s; }
.contact-item:nth-child(4) { animation-delay: 0.9s; }

.contact-item:hover {
  transform: translateX(10px);
}

.contact-item i {
  width: 60px;
  height: 60px;
  background: linear-gradient(45deg, #f093fb, #f5576c);
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  font-size: 1.3rem;
  transition: all 0.4s ease;
  animation: iconPulse 2s ease-in-out infinite;
  box-shadow: 0 5px 20px rgba(240, 147, 251, 0.3);
}

.contact-item:hover i {
  transform: scale(1.1);
  background: linear-gradient(45deg, #2980b9, #1abc9c);
  animation: formFocus 1s ease-out;
}

.contact-item h4 {
  color: #2c3e50;
  margin: 0 0 0.5rem 0;
  font-size: 1.1rem;
  font-weight: 600;
  transition: all 0.3s ease;
}

.contact-item:hover h4 {
  color: #3498db;
}

.contact-item p {
  margin: 0;
  color: #555;
  transition: all 0.3s ease;
}

.contact-item:hover p {
  color: #2c3e50;
}

.social-links {
  display: flex;
  gap: 1rem;
  margin-top: 2rem;
  opacity: 0;
  animation: contactSlideIn 1s ease-out 1.1s both;
}

.social-links a {
  width: 55px;
  height: 55px;
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(10px);
  border: 2px solid rgba(52, 152, 219, 0.2);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #2c3e50;
  font-size: 1.3rem;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  text-decoration: none;
  position: relative;
  overflow: hidden;
}

.social-links a::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(45deg, #3498db, #2980b9);
  border-radius: 50%;
  transform: scale(0);
  transition: transform 0.3s ease;
}

.social-links a:hover::before {
  transform: scale(1);
}

.social-links a:hover {
  color: white;
  border-color: #3498db;
  transform: translateY(-5px) scale(1.1);
  box-shadow: 0 10px 30px rgba(52, 152, 219, 0.4);
}

.social-links a i {
  position: relative;
  z-index: 1;
}

.contact-form {
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(20px);
  padding: 2.5rem;
  border-radius: 20px;
  box-shadow: 0 15px 50px rgba(0,0,0,0.1);
  border: 1px solid rgba(255, 255, 255, 0.3);
  animation: contactSlideInRight 1s ease-out;
  transition: all 0.3s ease;
}

.contact-form:hover {
  transform: translateY(-5px);
  box-shadow: 0 25px 70px rgba(0,0,0,0.15);
}

.form-group {
  margin-bottom: 1.5rem;
  opacity: 0;
  animation: fadeInScale 0.8s ease-out both;
}

.form-group:nth-child(1) { animation-delay: 0.3s; }
.form-group:nth-child(2) { animation-delay: 0.4s; }
.form-group:nth-child(3) { animation-delay: 0.5s; }
.form-group:nth-child(4) { animation-delay: 0.6s; }

.form-group input,
.form-group textarea {
  width: 100%;
  padding: 1.2rem;
  border: 2px solid #e9ecef;
  border-radius: 12px;
  font-size: 1rem;
  transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  font-family: inherit;
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(10px);
  position: relative;
}

.form-group input:focus,
.form-group textarea:focus {
  outline: none;
  border-color: #3498db;
  background: white;
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(52, 152, 219, 0.2);
  animation: formFocus 1s ease-out;
}

.form-group input::placeholder,
.form-group textarea::placeholder {
  color: #aaa;
  transition: all 0.3s ease;
}

.form-group input:focus::placeholder,
.form-group textarea:focus::placeholder {
  color: transparent;
  transform: translateY(-20px);
}

.form-group textarea {
  resize: vertical;
  min-height: 140px;
}

.btn-submit {
  width: 100%;
  padding: 1.2rem 2rem;
  background: linear-gradient(45deg, #3498db, #2980b9);
  color: white;
  border: none;
  border-radius: 12px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  position: relative;
  overflow: hidden;
  opacity: 0;
  animation: fadeInScale 0.8s ease-out 0.7s both;
  box-shadow: 0 8px 25px rgba(52, 152, 219, 0.3);
}

.btn-submit::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
  transition: left 0.6s;
}

.btn-submit:hover::before {
  left: 100%;
}

.btn-submit:hover {
  background: linear-gradient(45deg, #2980b9, #1abc9c);
  transform: translateY(-3px) scale(1.02);
  box-shadow: 0 15px 40px rgba(52, 152, 219, 0.4);
}

.btn-submit:active {
  transform: translateY(-1px) scale(0.98);
}

@media (max-width: 768px) {
  .contact-content {
    grid-template-columns: 1fr;
    gap: 2rem;
  }

  .contact-form {
    padding: 1.5rem;
  }

  .contact-item {
    gap: 0.8rem;
  }

  .contact-item i {
    width: 40px;
    height: 40px;
    font-size: 1rem;
  }
}

src/components/Contact.jsx.content:
import React, { useState } from 'react';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Create mailto link with form data
    const subject = encodeURIComponent(formData.subject || 'Contact from Portfolio');
    const body = encodeURIComponent(
      `Name: ${formData.name}\n` +
      `Email: ${formData.email}\n\n` +
      `Message:\n${formData.message}`
    );

    window.location.href = `mailto:sandhya@gmail.com?subject=${subject}&body=${body}`;

    // Reset form
    setFormData({
      name: '',
      email: '',
      subject: '',
      message: ''
    });
  };

  return (
    <section id="contact" className="contact">
      <div className="container">
        <h2 className="section-title">Get In Touch</h2>
        <div className="contact-content">
          <div className="contact-info">
            <h3>Let's Work Together</h3>
            <p>
              I'm always interested in new opportunities and exciting projects. 
              Whether you have a question or just want to say hi, feel free to reach out!
            </p>

            <div className="contact-item">
              <i className="fas fa-envelope"></i>
              <div>
                <h4>Email</h4>
                <p>sandhya@gmail.com</p>
              </div>
            </div>

            <div className="contact-item">
              <i className="fas fa-map-marker-alt"></i>
              <div>
                <h4>Location</h4>
                <p>Arghakhanchi, Nepal</p>
              </div>
            </div>

            <div className="social-links">
              <a href="mailto:sandhya@gmail.com" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-google"></i>
              </a>
              <a href="https://github.com/sandhyashrestha" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-github"></i>
              </a>
              <a href="https://instagram.com/sandhyashrestha" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-instagram"></i>
              </a>
              <a href="https://facebook.com/sandhyashrestha" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-facebook"></i>
              </a>
            </div>
          </div>

          <form className="contact-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="text"
                name="name"
                placeholder="Your Name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <input
                type="email"
                name="email"
                placeholder="Your Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <input
                type="text"
                name="subject"
                placeholder="Subject"
                value={formData.subject}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <textarea
                name="message"
                placeholder="Your Message"
                rows="6"
                value={formData.message}
                onChange={handleChange}
                required
              ></textarea>
            </div>

            <button type="submit" className="btn-submit">
              Send Message
              <i className="fas fa-paper-plane"></i>
            </button>
          </form>
        </div>
      </div>
    </section>
  );
};

export default Contact;

