src/components/Gallery.css.content:
.gallery {
  padding: 80px 0;
  background: linear-gradient(135deg, #764ba2 0%, #667eea 100%);
  position: relative;
  overflow: hidden;
}

.gallery .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 1;
}

.section-subtitle {
  text-align: center;
  color: rgba(255, 255, 255, 0.8);
  font-size: 1.1rem;
  margin-bottom: 3rem;
  font-weight: 300;
}

.gallery-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
}

.gallery-item {
  position: relative;
  height: 250px;
  cursor: pointer;
  border-radius: 15px;
  overflow: hidden;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

.gallery-item:hover {
  transform: translateY(-10px) scale(1.02);
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
}

.gallery-image {
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  transition: all 0.4s ease;
}

.gallery-image i {
  font-size: 4rem;
  color: white;
  transition: all 0.4s ease;
}

.gallery-item:hover .gallery-image i {
  transform: scale(1.2);
}

.gallery-overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(45deg, rgba(102, 126, 234, 0.9), rgba(118, 75, 162, 0.9));
  opacity: 0;
  transition: all 0.4s ease;
  display: flex;
  align-items: center;
  justify-content: center;
}

.gallery-item:hover .gallery-overlay {
  opacity: 1;
}

.gallery-content {
  text-align: center;
  color: white;
  transform: translateY(20px);
  transition: all 0.4s ease;
}

.gallery-item:hover .gallery-content {
  transform: translateY(0);
}

.gallery-content h3 {
  font-size: 1.3rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
}

.category {
  font-size: 0.9rem;
  opacity: 0.8;
  padding: 0.3rem 1rem;
  background: rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  display: inline-block;
}

/* Modal Styles */
.gallery-modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.9);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  opacity: 0;
  animation: fadeIn 0.3s ease-out forwards;
}

.modal-content {
  background: white;
  border-radius: 20px;
  max-width: 600px;
  width: 90%;
  max-height: 80vh;
  overflow-y: auto;
  position: relative;
  transform: scale(0.8);
  animation: scaleIn 0.3s ease-out forwards;
}

.close-btn {
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: rgba(0, 0, 0, 0.1);
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 1001;
}

.close-btn:hover {
  background: rgba(0, 0, 0, 0.2);
  transform: scale(1.1);
}

.modal-image {
  height: 300px;
  background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 20px 20px 0 0;
}

.modal-image i {
  font-size: 5rem;
  color: white;
}

.modal-info {
  padding: 2rem;
}

.modal-info h3 {
  color: #2c3e50;
  font-size: 1.8rem;
  margin-bottom: 0.5rem;
  font-weight: 600;
}

.modal-category {
  color: #667eea;
  font-size: 1rem;
  font-weight: 500;
  background: rgba(102, 126, 234, 0.1);
  padding: 0.3rem 1rem;
  border-radius: 20px;
  display: inline-block;
  margin-bottom: 1rem;
}

.modal-info p {
  color: #666;
  line-height: 1.6;
  font-size: 1.1rem;
}

@keyframes fadeIn {
  to {
    opacity: 1;
  }
}

@keyframes scaleIn {
  to {
    transform: scale(1);
  }
}

@media (max-width: 768px) {
  .gallery-grid {
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
  }

  .gallery-item {
    height: 200px;
  }

  .gallery-image i {
    font-size: 3rem;
  }

  .modal-content {
    width: 95%;
    margin: 1rem;
  }

  .modal-image {
    height: 200px;
  }

  .modal-image i {
    font-size: 3.5rem;
  }

  .modal-info {
    padding: 1.5rem;
  }

  .modal-info h3 {
    font-size: 1.5rem;
  }
}

src/components/Gallery.jsx.content:
import React, { useState } from 'react';
import './Gallery.css';

const Gallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  const galleryImages = [
    {
      id: 1,
      title: 'Coffee Website Design',
      category: 'Web Design',
      image: 'fas fa-coffee',
      description: 'Modern coffee shop website with clean aesthetics'
    },
    {
      id: 2,
      title: 'E-commerce Interface',
      category: 'UI/UX',
      image: 'fas fa-shopping-bag',
      description: 'User-friendly shopping experience design'
    },
    {
      id: 3,
      title: 'Mobile App UI',
      category: 'Mobile Design',
      image: 'fas fa-mobile-alt',
      description: 'Responsive mobile application interface'
    },
    {
      id: 4,
      title: 'Dashboard Design',
      category: 'UI/UX',
      image: 'fas fa-chart-line',
      description: 'Analytics dashboard with data visualization'
    },
    {
      id: 5,
      title: 'Portfolio Website',
      category: 'Web Design',
      image: 'fas fa-laptop-code',
      description: 'Creative portfolio with modern animations'
    },
    {
      id: 6,
      title: 'Brand Identity',
      category: 'Branding',
      image: 'fas fa-palette',
      description: 'Complete brand identity and logo design'
    }
  ];

  const openModal = (image) => {
    setSelectedImage(image);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  return (
    <section id="gallery" className="gallery">
      <div className="container">
        <h2 className="section-title">Gallery</h2>
        <p className="section-subtitle">A showcase of my creative work and designs</p>

        <div className="gallery-grid">
          {galleryImages.map((image) => (
            <div 
              key={image.id} 
              className="gallery-item"
              onClick={() => openModal(image)}
            >
              <div className="gallery-image">
                <i className={image.image}></i>
                <div className="gallery-overlay">
                  <div className="gallery-content">
                    <h3>{image.title}</h3>
                    <span className="category">{image.category}</span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {selectedImage && (
          <div className="gallery-modal" onClick={closeModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <button className="close-btn" onClick={closeModal}>
                <i className="fas fa-times"></i>
              </button>
              <div className="modal-image">
                <i className={selectedImage.image}></i>
              </div>
              <div className="modal-info">
                <h3>{selectedImage.title}</h3>
                <span className="modal-category">{selectedImage.category}</span>
                <p>{selectedImage.description}</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Gallery;

src/components/Header.css.content:
.header {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  background: linear-gradient(135deg, rgba(102, 126, 234, 0.95), rgba(118, 75, 162, 0.95));
  backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(240, 147, 251, 0.3);
  z-index: 1000;
  transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  transform: translateY(-100%);
  animation: slideDown 1s ease-out 0.5s both;
}

@keyframes slideDown {
  to {
    transform: translateY(0);
  }
}

@keyframes logoGlow {
  0%, 100% { text-shadow: 0 0 10px rgba(52, 152, 219, 0.3); }
  50% { text-shadow: 0 0 20px rgba(52, 152, 219, 0.6); }
}

@keyframes navFadeIn {
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.header.scrolled {
  background: linear-gradient(135deg, rgba(102, 126, 234, 0.98), rgba(118, 75, 162, 0.98));
  box-shadow: 0 4px 30px rgba(240, 147, 251, 0.3);
  backdrop-filter: blur(25px);
}

.header .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 70px;
}

.logo {
  font-size: 1.6rem;
  font-weight: 700;
  color: white;
  text-decoration: none;
  transition: all 0.4s ease;
  position: relative;
  background: linear-gradient(45deg, #ffffff, #f093fb);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: logoGlow 3s ease-in-out infinite;
}

.logo:hover {
  transform: scale(1.05);
}

.nav ul {
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
  gap: 2rem;
}

.nav li {
  opacity: 0;
  animation: navFadeIn 0.6s ease-out both;
}

.nav li:nth-child(1) { animation-delay: 0.8s; }
.nav li:nth-child(2) { animation-delay: 0.9s; }
.nav li:nth-child(3) { animation-delay: 1s; }
.nav li:nth-child(4) { animation-delay: 1.1s; }
.nav li:nth-child(5) { animation-delay: 1.2s; }

.nav a {
  color: white;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  position: relative;
  padding: 0.5rem 1rem;
  border-radius: 25px;
}

.nav a::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(45deg, #f093fb, #f5576c);
  border-radius: 25px;
  transform: scaleX(0);
  transition: transform 0.3s ease;
  z-index: -1;
}

.nav a::after {
  content: '';
  position: absolute;
  bottom: -5px;
  left: 50%;
  transform: translateX(-50%);
  width: 0;
  height: 3px;
  background: linear-gradient(45deg, #f093fb, #f5576c);
  border-radius: 2px;
  transition: width 0.3s ease;
}

.nav a:hover {
  color: white;
  transform: translateY(-2px);
}

.nav a:hover::before {
  transform: scaleX(1);
}

.nav a:hover::after {
  width: 80%;
}

.menu-toggle {
  display: none;
  flex-direction: column;
  cursor: pointer;
  gap: 4px;
}

.menu-toggle span {
  width: 25px;
  height: 3px;
  background: white;
  transition: 0.3s;
}

@media (max-width: 768px) {
  .menu-toggle {
    display: flex;
  }

  .nav {
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    background: linear-gradient(135deg, rgba(102, 126, 234, 0.98), rgba(118, 75, 162, 0.98));
    box-shadow: 0 2px 10px rgba(240, 147, 251, 0.3);
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.3s ease;
  }

  .nav-open {
    max-height: 300px;
  }

  .nav ul {
    flex-direction: column;
    padding: 1rem 2rem;
    gap: 1rem;
  }
}

src/components/Header.jsx.content:
import React, { useState } from 'react';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    element.scrollIntoView({ behavior: 'smooth' });
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="container">
        <div className="logo">
          <h2>Sandhya Shrestha</h2>
        </div>
        <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
          <ul>
            <li><a href="#home" onClick={() => scrollToSection('home')}>Home</a></li>
            <li><a href="#about" onClick={() => scrollToSection('about')}>About</a></li>
            <li><a href="#skills" onClick={() => scrollToSection('skills')}>Skills</a></li>
            <li><a href="#projects" onClick={() => scrollToSection('projects')}>Projects</a></li>
            <li><a href="#gallery" onClick={() => scrollToSection('gallery')}>Gallery</a></li>
            <li><a href="#contact" onClick={() => scrollToSection('contact')}>Contact</a></li>
          </ul>
        </nav>
        <div className="menu-toggle" onClick={toggleMenu}>
          <span></span>
          <span></span>
          <span></span>
        </div>
      </div>
    </header>
  );
};

export default Header;

src/components/Home.css.content:
.home {
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  align-items: center;
  position: relative;
  overflow: hidden;
}

.home::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    radial-gradient(circle at 20% 20%, rgba(0, 255, 255, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 80%, rgba(255, 0, 150, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 50% 50%, rgba(0, 150, 255, 0.05) 0%, transparent 50%);
  animation: morphBackground 15s ease-in-out infinite;
}

.home::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect x="10" y="10" width="4" height="4" fill="rgba(0,255,255,0.3)" opacity="0.8"><animate attributeName="opacity" values="0.8;0.2;0.8" dur="3s" repeatCount="indefinite"/></rect><rect x="70" y="30" width="3" height="3" fill="rgba(255,0,150,0.4)" opacity="0.6"><animate attributeName="opacity" values="0.6;1;0.6" dur="4s" repeatCount="indefinite"/></rect><rect x="30" y="70" width="2" height="2" fill="rgba(0,150,255,0.5)" opacity="0.7"><animate attributeName="opacity" values="0.7;0.3;0.7" dur="5s" repeatCount="indefinite"/></rect></svg>');
  animation: slidePattern 20s linear infinite;
}

@keyframes morphBackground {
  0%, 100% { 
    background: 
      radial-gradient(circle at 20% 20%, rgba(0, 255, 255, 0.1) 0%, transparent 50%),
      radial-gradient(circle at 80% 80%, rgba(255, 0, 150, 0.1) 0%, transparent 50%);
  }
  50% { 
    background: 
      radial-gradient(circle at 80% 20%, rgba(255, 0, 150, 0.15) 0%, transparent 50%),
      radial-gradient(circle at 20% 80%, rgba(0, 255, 255, 0.15) 0%, transparent 50%);
  }
}

@keyframes slidePattern {
  0% { transform: translateX(-100px); }
  100% { transform: translateX(100px); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(50px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeInRight {
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
}

@keyframes shimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}

.home .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 1;
}

.home-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
}

.intro {
  animation: fadeInUp 1s ease-out;
}

.intro h1 {
  font-size: 3rem;
  color: white;
  margin-bottom: 1rem;
  line-height: 1.2;
  animation: fadeInUp 1s ease-out 0.2s both;
}

.intro h1 .highlight {
  color: #f093fb;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
  background: linear-gradient(45deg, #f093fb, #f5576c, #4facfe);
  background-size: 300% 100%;
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: colorWave 4s ease-in-out infinite;
}

.intro h2 {
  font-size: 1.5rem;
  color: rgba(255, 255, 255, 0.9);
  margin-bottom: 1.5rem;
  font-weight: 400;
  animation: slideInLeft 1s ease-out 0.4s both;
  position: relative;
  overflow: hidden;
}

.intro h2::after {
  content: '|';
  color: #00ffff;
  animation: pulse 1.5s infinite, slideInLeft 1s ease-out 0.4s both;
  margin-left: 2px;
}

@keyframes colorWave {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-80px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(80px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.intro p {
  font-size: 1.1rem;
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
  margin-bottom: 2rem;
  animation: slideInLeft 1s ease-out 0.6s both;
}

.social-links {
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  animation: slideInLeft 1s ease-out 0.8s both;
}

.social-links a {
  width: 50px;
  height: 50px;
  background: rgba(255, 255, 255, 0.1);
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.2rem;
  text-decoration: none;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  backdrop-filter: blur(10px);
}

.social-links a:hover {
  background: rgba(240, 147, 251, 0.2);
  border-color: #f093fb;
  color: #f093fb;
  transform: translateY(-5px) scale(1.1);
  box-shadow: 0 10px 30px rgba(240, 147, 251, 0.4);
}

.cta-buttons {
  display: flex;
  gap: 1rem;
  animation: slideInLeft 1s ease-out 1s both;
}

.btn-primary,
.btn-secondary {
  padding: 1rem 2rem;
  border: none;
  border-radius: 30px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  text-decoration: none;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  position: relative;
  overflow: hidden;
}

.btn-primary {
  background: linear-gradient(45deg, #667eea, #764ba2);
  color: white;
  padding: 15px 30px;
  border: none;
  border-radius: 50px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  text-decoration: none;
  display: inline-block;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
  animation: buttonGlow 2s ease-in-out infinite;
}

.btn-primary:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 25px rgba(17, 153, 142, 0.6);
}

.btn-secondary {
  background: transparent;
  color: white;
  padding: 15px 30px;
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-radius: 50px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  text-decoration: none;
  display: inline-block;
  backdrop-filter: blur(10px);
}

.btn-secondary:hover {
  background: rgba(17, 153, 142, 0.3);
  border-color: rgba(17, 153, 142, 0.6);
  transform: translateY(-3px);
}

.hero-image {
  display: flex;
  justify-content: center;
  align-items: center;
  animation: slideInRight 1s ease-out 1.2s both;
}

.image-placeholder {
  width: 350px;
  height: 350px;
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  animation: bounceFloat 4s ease-in-out infinite;
  background-image: url('https://via.placeholder.com/350'); /* Placeholder image */
  background-size: cover;
  background-position: center;
  border-radius: 10px; /* Optional: for rounded corners */
  box-shadow: 0 0 20px rgba(0,0,0,0.3);
}

.profile-avatar {
  position: relative;
  width: 280px;
  height: 280px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.avatar-ring {
  position: absolute;
  width: 100%;
  height: 100%;
  border: 4px solid transparent;
  border-top: 4px solid #00ffff;
  border-right: 4px solid rgba(0, 255, 255, 0.6);
  border-radius: 50%;
  animation: spinGlow 6s linear infinite;
}

.avatar-ring::before {
  content: '';
  position: absolute;
  top: -6px;
  left: -6px;
  right: -6px;
  bottom: -6px;
  border: 2px solid transparent;
  border-bottom: 2px solid rgba(255, 0, 150, 0.4);
  border-left: 2px solid rgba(0, 150, 255, 0.3);
  border-radius: 50%;
  animation: spinGlow 10s linear infinite reverse;
}

.avatar-inner {
  width: 200px;
  height: 200px;
  background: linear-gradient(135deg, rgba(0,255,255,0.2) 0%, rgba(255,0,150,0.1) 100%);
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 5rem;
  color: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(20px);
  border: 2px solid rgba(0, 255, 255, 0.3);
  box-shadow: 
    0 0 30px rgba(0, 255, 255, 0.4),
    inset 0 0 30px rgba(255, 0, 150, 0.2);
  animation: neonPulse 3s ease-in-out infinite;
  transition: all 0.3s ease;
}

.avatar-inner:hover {
  transform: scale(1.05);
  box-shadow: 
    0 0 50px rgba(0, 255, 255, 0.7),
    inset 0 0 40px rgba(255, 0, 150, 0.3);
}

@keyframes spinGlow {
  0% { 
    transform: rotate(0deg);
    filter: drop-shadow(0 0 10px rgba(0, 255, 255, 0.5));
  }
  50% { 
    filter: drop-shadow(0 0 20px rgba(255, 0, 150, 0.7));
  }
  100% { 
    transform: rotate(360deg);
    filter: drop-shadow(0 0 10px rgba(0, 255, 255, 0.5));
  }
}

@keyframes bounceFloat {
  0%, 100% { 
    transform: translateY(0px) scale(1);
  }
  25% { 
    transform: translateY(-15px) scale(1.02);
  }
  50% { 
    transform: translateY(-8px) scale(1.01);
  }
  75% { 
    transform: translateY(-12px) scale(1.02);
  }
}

@keyframes neonPulse {
  0%, 100% { 
    box-shadow: 
      0 0 30px rgba(0, 255, 255, 0.4),
      inset 0 0 30px rgba(255, 0, 150, 0.2);
  }
  50% { 
    box-shadow: 
      0 0 60px rgba(0, 255, 255, 0.8),
      inset 0 0 50px rgba(255, 0, 150, 0.4);
  }
}


@media (max-width: 768px) {
  .home-content {
    grid-template-columns: 1fr;
    text-align: center;
    gap: 2rem;
  }

  .intro h1 {
    font-size: 2.5rem;
  }

  .intro h2 {
    font-size: 1.2rem;
  }

  .cta-buttons {
    justify-content: center;
    flex-wrap: wrap;
  }

  .btn-primary, .btn-secondary {
    width: 80%;
    margin: 0.5rem 0;
  }

  .hero-image {
    margin-top: 3rem;
  }

  .image-placeholder {
    width: 250px;
    height: 250px;
  }

  .profile-avatar {
    width: 200px;
    height: 200px;
  }

  .avatar-inner {
    width: 150px;
    height: 150px;
    font-size: 3.5rem;
  }
}

src/components/Projects.css.content:
.projects {
  padding: 80px 0;
  background: linear-gradient(135deg, #4facfe 0%, #00f2fe 50%, #764ba2 100%);
  position: relative;
  overflow: hidden;
}

.projects::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 80 80"><polygon points="40,10 60,50 20,50" fill="rgba(255,255,255,0.05)"/></svg>');
  animation: float 10s ease-in-out infinite;
}



.projects .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 1;
}

.projects .section-title {
  color: white;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

.projects .section-title::after {
  background: linear-gradient(45deg, #fff, #ffd700, #fff);
}

.projects-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
  perspective: 1000px;
}

.project-card {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0,0,0,0.2);
  transition: all 0.6s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  cursor: pointer;
  opacity: 1;
  position: relative;
  border: 1px solid rgba(255, 255, 255, 0.3);
  transform-style: preserve-3d;
}

.project-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(45deg, transparent, rgba(255,255,255,0.1), transparent);
  transform: translateX(-100%);
  transition: transform 0.6s;
  z-index: 1;
}

.project-card:hover::before {
  transform: translateX(100%);
}

.project-card:hover {
  transform: translateY(-20px) scale(1.02);
  box-shadow: 0 25px 80px rgba(0,0,0,0.3);
}

.project-image {
  height: 220px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 3.5rem;
  color: white;
  position: relative;
  transition: all 0.4s ease;
}

.project-card:hover .project-image {
  transform: scale(1.1);
}

.project-image::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(45deg, transparent 30%, rgba(255,255,255,0.2) 50%, transparent 70%);
  transform: translateX(-100%);
  transition: transform 0.8s;
}

.project-card:hover .project-image::after {
  transform: translateX(100%);
}

.project-content {
  padding: 2rem;
  position: relative;
  z-index: 2;
}

.project-content h3 {
  color: #2c3e50;
  margin-bottom: 0.8rem;
  font-size: 1.4rem;
  font-weight: 600;
  transition: all 0.3s ease;
}

.project-card:hover .project-content h3 {
  color: #3498db;
  transform: translateX(5px);
}

.project-content p {
  color: #666;
  line-height: 1.6;
  margin-bottom: 1.5rem;
  transition: all 0.3s ease;
}

.project-card:hover .project-content p {
  color: #555;
}

.project-tech {
  display: flex;
  flex-wrap: wrap;
  gap: 0.8rem;
  margin-bottom: 1.5rem;
}

.tech-tag {
  background: linear-gradient(45deg, #3498db, #2980b9);
  color: white;
  padding: 0.4rem 1rem;
  border-radius: 20px;
  font-size: 0.85rem;
  font-weight: 500;
  transition: all 0.3s ease;
}

.project-card:hover .tech-tag {
  transform: scale(1);
  background: linear-gradient(45deg, #2980b9, #1abc9c);
}

.project-links {
  display: flex;
  gap: 1.5rem;
}

.project-links a {
  color: #3498db;
  text-decoration: none;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  padding: 0.5rem 1rem;
  border-radius: 25px;
  background: rgba(52, 152, 219, 0.1);
}

.project-links a:hover {
  color: white;
  background: #3498db;
  transform: translateY(-3px);
  box-shadow: 0 8px 25px rgba(52, 152, 219, 0.3);
}

@media (max-width: 768px) {
  .projects-grid {
    grid-template-columns: 1fr;
    gap: 1.5rem;
  }

  .project-image {
    height: 150px;
    font-size: 3rem;
  }

  .project-content {
    padding: 1.5rem;
  }
}

@keyframes float {
  0% { transform: translateY(0px) rotateZ(0deg); }
  50% { transform: translateY(-10px) rotateZ(5deg); }
  100% { transform: translateY(0px) rotateZ(0deg); }
}

src/components/Projects.jsx.content:
import React from 'react';
import './Projects.css';

const Projects = () => {
  const projects = [
    {
      id: 1,
      title: 'Coffee Website',
      description: 'A modern coffee shop website with responsive design and interactive elements.',
      image: 'fas fa-coffee',
      technologies: ['HTML', 'CSS', 'JavaScript', 'Bootstrap'],
      link: 'https://coffee-website-demo.com'
    },
    {
      id: 2,
      title: 'Portfolio Website',
      description: 'A creative portfolio website showcasing modern design principles.',
      image: 'fas fa-laptop-code',
      technologies: ['React.js', 'CSS', 'JavaScript'],
      link: 'https://portfolio-demo.com'
    },
    {
      id: 3,
      title: 'E-commerce Site',
      description: 'A full-featured e-commerce website with shopping cart functionality.',
      image: 'fas fa-shopping-cart',
      technologies: ['React.js', 'CSS', 'JavaScript', 'Bootstrap'],
      link: 'https://ecommerce-demo.com'
    },
    {
      id: 4,
      title: 'Business Website',
      description: 'A professional business website with clean design and user-friendly interface.',
      image: 'fas fa-building',
      technologies: ['HTML', 'CSS', 'JavaScript', 'WordPress'],
      link: 'https://business-demo.com'
    }
  ];

  const handleProjectClick = (link) => {
    window.open(link, '_blank', 'noopener,noreferrer');
  };

  return (
    <section id="projects" className="projects">
      <div className="container">
        <h2 className="section-title">My Projects</h2>
        <div className="projects-grid">
          {projects.map((project) => (
            <div 
              key={project.id} 
              className="project-card"
              onClick={() => handleProjectClick(project.link)}
            >
              <div className="project-image">
                <i className={project.image}></i>
              </div>
              <div className="project-content">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <div className="technologies">
                  {project.technologies.map((tech, index) => (
                    <span key={index} className="tech-tag">{tech}</span>
                  ))}
                </div>
                <div className="project-link">
                  <span>View Project</span>
                  <i className="fas fa-external-link-alt"></i>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;

